INSERT INTO "public"."original_posts" VALUES ('1289316201184436', null, null, null, null, 'កាលពីពេលថ្មីៗនេះមានគណនីហ្វេសបុកម្នាក់បានបង្ហោះវិដេអូទាក់ទងនឹងករណីអ្នកបើកម៉ូតូកង់បី ដែលបានបុកកាងឡានក្រោយរបស់គាត់ រហូតដល់បែកស្តុបតូចមួយខាងក្រោមកាង។ ឡានគេឡានយើងជារបស់ដែលស្រឡាញ់ដូចគ្នា ដូចនេះពេលប៉ះពារគាត់ប្រាកដជាខឹងមិនខាន ព្រោះត្រូវចំណាយលុយធ្វើ ខាតពេលវេលា ខាតទាំងថ្នាំឡានហ្សីនទៀតផង។ គាត់បានរៀបរាប់នៅលើវិដេអូថា៖ " បុកហើយ អាងរត់ បុកហើយ ពេលចាប់បាន ថាក្រត្រឹមអ្នករត់តុកតុក អីតិចអាង អត់លុយ សូមទោសគ្មានចេតនា អីតិចអាងទឹកភ្នែក តែពេលពូ ពូ កង់បី ឯងជិះវិញម៉ែឪ អាឈ្លើយ គ្មានមើលអាណាទេ គិតមើលណាពូ ពេលគេទារអោយសង ត្រឹម 20$ 30$ ពូថាគ្មានលុយផង រក2ថ្ងៃ មិនបានផង តែបើដឹងខ្លួនគ្មានសម្ថតភាពហើយ មិចក៏មិនចេះប្រុងប្រយ័ត្ន ហើយសម្ដីឈ្លើយទៀត ចុះពូមានគិតថាទំរាំគេរកលុយទិញបានឡាន រ៉ាប់មុឺន $ និងគេស្រូលណាស់មែន។ មិនមែនគ្រប់គ្នាចិត្តធម៌ បុកខូចឡាន ហើយឲលុយថែមទេ ក្រែងចាប់អាជីពមួយទៀត បុកហើយរត់ បុកហើយហៅគ្នាមកតវ៉ា៕"  ប្រភព៖ Cheata Mony', null, null, null, null, null, '16411', '0', '15', '2017-04-08T11:32:36+0000', null, 'article', null, null, null, null, null, null, '1273063879457293_1275330532563961');
