INSERT INTO "public"."original_posts" VALUES ('1460603330663139', null, null, null, null, 'ភ្នំពេញ៖ ក្មេងសិស្សសាលាម្នាក់រងរបួសជើង បន្ទាប់ពីដើរថ្មើជើងចេញពីសលារៀន បំណង់ឆ្លងផ្លូវ មុននឹងឡើងឆ្លងស្ពានអកាសឆ្លងផ្លូវបន្តទៀត ក៏ត្រូវបានម៉ូតូបើកបុក ដួលរងរបួសជើង ហើយព្យាយាមបើករត់ មិនទទួលខុសត្រូវ ត្រូវបានអ្នកឃើញហេតុការណ៍ ដេញតាមឃាត់ យកម៉ូតូប្រគល់ឲ្យប៉ូលិស ។ ហេតុការណ៍ខាងលើកើតឡើង កាលពីវេលាម៉ោង១១និង១០នាទីព្រឹកថ្ងៃទី២៤ ខែមេសា ឆ្នាំ២០១៧ ស្ថិតនៅស្ពានអាកាស ផ្លូវជាតិលេខ៤ មុខវិទ្យាល័យជម្ពូវ័ន សង្កាត់ចោមចៅ ខណ្ឌពោធិ៍សែនជ័យ ។ ក្មេងប្រុសរងគ្រោះ គ្រាន់តែដឹងថា អាយុ ៧ឆ្នាំរៀនថ្នាក់ទី២ សាលាបឋមសិក្សាជម្ពូវ័ន មានទីលំនៅ ខាងក្រោយផ្សារជម្ពូវ័ន សង្កាត់ចោមចៅ រងរបួសជើង ។ ចំណែកម៉ូតូបង្ក ម៉ាកហុងដា គុបមូលតឿ ពណ៌ទឹកដោះគោ ពាក់ស្លាកលេខ ភ្នំពេញ 1BP-1854 ជិះដោយបុរសចំណាស់ម្នាក់ និងរួមដំណើរដោយក្មេង ២នាក់ពីក្រោយទៀត ។ អ្នកឃើញហេតុការណ៍បានឲ្យដឹងថា មុនកើតហេតុ វេលាដែលក្មេងសិស្សសាលា ចេញទៅផ្ទះ ហើយក្មេងរងគ្រោះ ដើរចេញពីសាលារៀន សរសៀចិញ្ចើមផ្លូវពីលិចទៅកើត ដើម្បីទៅឆ្លងផ្លូវ និងឆ្លងស្ពានអាកាសដ៏ទំនើបរបស់សាលាក្រុងបន្តទៀត ហើយពេលដែលក្មេងខាងលើកំពុង ដើរឆ្លងផ្លូវ…', null, null, null, null, null, '31287', '0', '76', '2017-04-24T08:24:35+0000', null, 'article', null, null, null, null, null, null, '1461342843898603_1504837482882472');
