INSERT INTO "public"."original_posts" VALUES ('1510337445654618', null, null, null, null, '(ព្យុងយ៉ាង)៖ នៅថ្ងៃទី១៥ ខែមេសា ឆ្នាំ២០១៧នេះ ប្រទេសកូរ៉េខាងជើង បានចាប់ផ្តើមធ្វើព្យុហយាត្រា យោធាដ៏ធំសម្បើម សម្រាប់ខួបកំណើតទី១០៥ឆ្នាំ នៃបិតាស្ថាបនិកកូរ៉េខាងជើង លោក គីម អ៊ីលស៊ុង ដោយមានការដង្ហែជាក្បួនមានទាំងកម្លាំង យោធាចំរុះ និងសព្វាវុធទំនើបៗ ព្រមទាំងមានការចូលរួមដោយផ្ទាល់ពី មេដឹកនាំកំពូលគីម ជុងអ៊ុន ចំពោះមុខអ្នកសារព័ត៌មាន និងភ្ញៀវបរទេសដែលមានបេសកកម្មនៅ ទីក្រុងព្យុងយ៉ាងរាប់រយរូបចូលរួម ផងដែរ។ នេះបើតាមការចេញផ្សាយ ដោយបណ្តាញសារព័ត៌មាន RT របស់រុស្ស៊ី ដែលមានកំពុង មានវត្តមាននៅទីនោះ ស្រាប់នាព្រឹកថ្ងៃសៅរ៍ ទី១៥ ខែមេសា ឆ្នាំ២០១៧។ នៅថ្ងៃទីនេះទី១៥ ស្អែកដែលជាថ្ងៃធំ ត្រូវនឹងខួបកំណើតទី ១០៥ឆ្នាំ នៃបិតាស្ថាបនិកកូរ៉េខាងជើង លោក គីម អ៊ីលស៊ុង នោះ អ្នកវិភាគជាច្រើន ដែលទទួលបានព័ត៌មានសម្ងាត់ ពីកូរ៉េខាងជើង និងការវិភាគដោយរូបភាពពី ប្រព័ន្ធផ្កាយរណបឲ្យដឹងថា លោក គីម ជុងអ៊ុន ដែលជាមេដឹកនាំត្រូកូល គីម ជំនាន់ទី៣ នឹងបញ្ជាឲ្យ បំផ្ទុះសាកល្បងអាវុធនុយក្លេអ៊ែរ ជាលើកទី៦...', null, null, null, null, null, '0', '0', '0', '2017-04-15T11:30:32+0000', null, 'website', null, null, null, null, null, null, '1304782302948719_1328698600557089');
