INSERT INTO "public"."original_posts" VALUES ('201592649916262_1444014559007392', 'លោក​ ហ៊ុន ​សែន ​បង្កើនវោហា​សាស្ត្រ​សង្គ្រាម​ នៅ​ពេល​ការ​បោះ​ឆ្នោត​ជិត​ចូល​មក​ដល់​', 'លោកនាយករដ្ឋមន្ត្រី ហ៊ុន សែន នៅថ្ងៃទី២៤មេសានេះ បានបន្តប្រើប្រាស់វោហាសាស្ត្រសង្គ្រាមនៅមុនពេលដែលប្រទេសកម្ពុជារៀបចំការបោះឆ្នោតនៅពេលខាងមុខ។ លោក ហ៊ុន សែន បានចោទជាសំណួរត្រង់ៗថា តើប្រជាពលរដ្ឋចង់បានការអភិវឌ្ឍន៍ សន្តិភាព ឬការជីកលេនដ្ឋានបាញ់គ្នាដូចអតីតកាល? ការលើកឡើងរបស់លោកនាយករដ្ឋមន្ត្រី បានធ្វើឡើងនៅក្នុងពិធីសម្ពោធដាក់ឲ្យប្រើប្រាស់នូវស្ពានមិត្តភាពកម្ពុជា-វៀតណាម ជ្រៃធំ-ឡុងប៊ិញ នៅចំពោះមុខនាយករដ្ឋមន្ត្រីវៀតណាម លោក ង្វៀន សួនហ្វ៊ុក ដែលកំពុងបំពេញទស្សនកិច្ចរយៈពេល៣ថ្ងៃនៅកម្ពុជា។', null, 'km.rfi.fr', 'លោក​នាយក​រដ្ឋមន្ត្រី ​ហ៊ុន សែន ​នៅ​​ថ្ងៃទី​២៤​មេសា​​នេះ ​បាន​​បន្ត​​ប្រើប្រាស់​​វោហា​សាស្ត្រ​​សង្គ្រាម​​​នៅ​មុន​​ពេល​ដែល​​ប្រទេស​កម្ពុជា​​រៀបចំ​​ការ​បោះឆ្នោត​​នៅ​ពេល​​ខាង​មុខ​។ ​លោក ​ហ៊ុន ​សែន ​​បាន​​ចោទ​​ជា​​សំណួរ​ត្រង់ៗ​ថា ​តើ​​ប្រជាពលរដ្ឋ​​ចង់​បាន​​ការ​អភិវឌ្ឍន៍...', '2017-04-24T06:39:28+0000', 'RFI ខេមរភាសា / RFI Khmer', '201592649916262', null, 'https://external.xx.fbcdn.net/safe_image.php?d=AQCyS_mn_C9BDcQl&w=130&h=130&url=http%3A%2F%2Fscd.km.rfi.fr%2Fsites%2Fkhmer.filesrfi%2Fimagecache%2Frfi_16x9_1024_578%2Fsites%2Fimages.rfi.fr%2Ffiles%2Faef_image%2Fhun_sen_fb.jpg&cfs=1&sx=125&sy=0&sw=578&sh=578&_nc_hash=AQDqv_62sBPCFjr9', '716', '2386', '352', null, null, 'link', 'https://www.facebook.com/RFIKhmer/posts/1444014559007392', 'http://rfi.my/16x2.F', null, null, null, null, '1242212409232216_1258751090911681');
