INSERT INTO "public"."original_posts" VALUES ('571623549548803_1500522936658855', 'The ICT Networking and Reunion Event', 'ភ្នំពេញ៖ នាល្ងាចថ្ងៃទី ០៧ ខែមេសា ឆ្នាំ២០១៧ មជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី បានរៀបកម្មវិធី The ICT Networking and Reunion Event ដោយមានការចូលរួមពីតំណាងនៃសាកលវិទ្យាល័យ ក្រុមហ៊ុនវិនិយោគលើវិស័យព័ត៌មានវិទ្យាក្នុង និងក្រៅស្រុក ព្រមទាំងនិស្សិតអាហារូបករណ៏ទាំង ៥ ជំនាន់របស់មជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី។ ក្នុងកម្មវិធីនេះផងដែរ ក៏មានការឡើងបង្ហាញអំពី ចំណាប់អារម្មណ៏ពីសំណាក់ភ្ញៀវកិត្តិយស និងអតីតនិស្សិតនៃមជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី។ នៅចុងបញ្ចប់នៃកម្មវិធី ក៏មានការរាំលេងកំសាន្តសប្បាយ ជុំគ្នាផងដែរ។', null, 'The ICT Networking and Reunion Event', 'ភ្នំពេញ៖ នាល្ងាចថ្ងៃទី ០៧ ខែមេសា ឆ្នាំ២០១៧ មជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី បានរៀបកម្មវិធី The ICT Networking and Reunion Event ដោយមានការចូលរួមពីតំណាងនៃសាកលវិទ្យាល័យ ក្រុមហ៊ុនវិនិយោគលើវិស័យព័ត៌មានវិទ្យាក្នុង និងក្រៅស្រុក ព្រមទាំងនិស្សិតអាហារូបករណ៏ទាំង ៥ ជំនាន់របស់មជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី។ ក្នុងកម្មវិធីនេះផងដែរ ក៏មានការឡើងបង្ហាញអំពី ចំណាប់អារម្មណ៏ពីសំណាក់ភ្ញៀវកិត្តិយស និងអតីតនិស្សិតនៃមជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី។ នៅចុងបញ្ចប់នៃកម្មវិធី ក៏មានការរាំលេងកំសាន្តសប្បាយ ជុំគ្នាផងដែរ។', '2017-04-10T10:05:29+0000', 'Korea Software HRD Center', '571623549548803', null, 'https://scontent.xx.fbcdn.net/v/t1.0-0/s130x130/17883524_1500523216658827_7269935599529704866_n.jpg?oh=0b160b121520f3865ed16f92d092a4c0&oe=598938B4', '18', '39', '0', null, null, 'photo', 'https://www.facebook.com/album.php?fbid=1500522936658855&id=571623549548803&aid=1073741980', 'https://www.facebook.com/ksignhrd/photos/a.1500522936658855.1073741980.571623549548803/1500523216658827/?type=3', null, null, null, null, '1230172403765238_1252826908166454');
