INSERT INTO "public"."posts" VALUES ('1136178779765352_1262009247182304', 'Korea Software HRD Center', null, 'Teckchun Aing shared Korea Software HRD Center''s album: The ICT Networking and Reunion Event.', null, 'ភ្នំពេញ៖ នាល្ងាចថ្ងៃទី ០៧ ខែមេសា ឆ្នាំ២០១៧ មជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី បានរៀបកម្មវិធី The ICT Networking and Reunion Event ដោយមានការចូលរួមពីតំណាងនៃសាកលវិទ្យាល័យ ក្រុមហ៊ុនវិនិយោគលើវិស័យព័ត៌មានវិទ្យាក្នុង និងក្រៅស្រុក ព្រមទាំងនិស្សិតអាហារូបករណ៏ទាំង ៥ ជំនាន់របស់មជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី។ ក្នុងកម្មវិធីនេះផងដែរ ក៏មានការឡើងបង្ហាញអំពី ចំណាប់អារម្មណ៏ពីសំណាក់ភ្ញៀវកិត្តិយស និងអតីតនិស្សិតនៃមជ្ឈមណ្ឌលកូរ៉េ សហ្វវែរ អេច អ ឌី។ នៅចុងបញ្ចប់នៃកម្មវិធី ក៏មានការរាំលេងកំសាន្តសប្បាយ ជុំគ្នាផងដែរ។', '2017-04-10T11:03:29+0000', 'Teckchun Aing', '1136178779765352', null, 'https://scontent.xx.fbcdn.net/v/t1.0-0/s130x130/17883524_1500523216658827_7269935599529704866_n.jpg?oh=0b160b121520f3865ed16f92d092a4c0&oe=598938B4', '0', '0', '0', null, 'mobile_status_update', null, 'https://www.facebook.com/1136178779765352/posts/1262009247182304', 'https://www.facebook.com/ksignhrd/photos/a.1500522936658855.1073741980.571623549548803/1500523216658827/?type=3', null, null, '571623549548803_1500522936658855', '2');
