INSERT INTO "public"."posts" VALUES ('1374116272631595_1379105635465992', 'Khmer Academy', null, 'KokPheng Yin and 8 others shared Khmer Academy''s photo.', null, 'Khmer Academy មានកិត្តិយស សូមជម្រាបជូនដំណឹងដល់អ្នកប្រើប្រាស់ និងជាពិសេសប្អូនៗដែលជាសិស្សានុសិស្សទាំងអស់ឲ្យបានជ្រាបថា ៖
