INSERT INTO "public"."posts" VALUES ('1836359866605266_1846549682252951', 'Sou Patriota desde quando', null, 'Chhorn Soklay shared Sou Patriota desde quando''s live video.', null, 'Curta Sou Patriota desde quando <3
